    public void FindmaxCustID(){
    try{
            Class.forName("com.mysql.jdbc.Driver");
            Connection con = (Connection)DriverManager.getConnection("jdbc:mysql://localhost:3306/Ivory","root","");
            Statement st=con.createStatement();
            
            String Sql1="Select CustID from Customer ;";
            String maxcustID=null;
            ResultSet rs1=st.executeQuery(Sql1);
            while(rs1.next()){
                
                maxcustID=rs1.getString("CustID");
                
         
            }
             maxCustid=Integer.parseInt(maxcustID);
            
           
            
        }catch(Exception e){
        JOptionPane.showMessageDialog(null, e);
        }
    }
    

