          public int FindmaxResID(){
              
    try{
            Class.forName("com.mysql.jdbc.Driver");
            Connection con = (Connection)DriverManager.getConnection("jdbc:mysql://localhost:3306/Ivory","root","");
            Statement st=con.createStatement();
            
            String Sql1="SELECT MAX(Res_ID) FROM Reservation;";
            String maxResID=null;
            
            ResultSet rs1=st.executeQuery(Sql1);  
            maxResID=rs1.getString("CustID");
          
            MaxResID=Integer.parseInt(maxResID);
            System.out.println(MaxResID);
             
        }catch(Exception e){
        
            }
            return MaxResID;
          }
    
